import { useEffect, useState } from "react";
import { FakeStoreApi } from "../../services/fake-store-api";
import { useSearchParams } from "react-router-dom";
import Item from "../item/item";
import { useCart } from "../../context/cart";

const Products = () => {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [query] = useSearchParams();
  const { addToCart, user, login } = useCart();

  const searchQuery = query.get("q");

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      const products = searchQuery
        ? await FakeStoreApi.fetchProductsBySearchQuery(searchQuery)
        : await FakeStoreApi.fetchAllProducts();
      setProducts(products);
      setLoading(false);
    };
    fetchProducts().catch(console.error);
  }, [searchQuery]);

  if (!loading && searchQuery && !products.length) {
    return (
      <div className="container">
        <div className="product py-2">
          <div className="details p-3">
            No products found matching your query.
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="container">
        <div className="products my-5">
          <div className="grid">
            {loading ? (
              <div className="loader" />
            ) : (
              products.map((product) => (
                <Item
                  key={product.id}
                  data={product}
                  addToCart={() => addToCart(product)}
                />
              ))
            )}
          </div>
        </div>
      </div>
      <div className="container">
        <div className="cart-login-container">
          <div className="cart">
            {/* Your existing cart component or link */}
            <Link to="/cart">View Cart</Link>
          </div>
          <div className="login">
            {/* Check if the user is logged in, if not, show the login button */}
            {user ? (
              <span>Welcome, {user.username}!</span>
            ) : (
              <button onClick={login}>Login</button>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Products;
